from pydub import AudioSegmentimport simpleaudio as saimport openaidef generate_audio(input_text, speed=1.15):      response = openai.audio.speech.create(    model="tts-1",    voice="alloy",    speed=speed,    input=input_text,    )    with open("tmp_output.Opus", "wb") as f:        f.write(response.content)    # Play the audio    audio = AudioSegment.from_mp3("tmp_output.Opus")    playback = sa.play_buffer(        audio.raw_data,        num_channels=audio.channels,        bytes_per_sample=audio.sample_width,        sample_rate=audio.frame_rate)    return playback.wait_done()  # Wait for the audio playback to finish